mapper.select:
select top ${pager.pageSize} * from( SELECT ROW_NUMBER() OVER (ORDER BY title asc) AS RowNumber,* from( select distinct s.id, s.title, s.keyword, s.description, '' contents, s.address, s.tel, s.contacter, s.imagePath, s.siteId, s.click, s.pingnum from shop s left join shoptype st on st.shopid = s.id left join type t on t.id = st.typeid
) t1 ) t
where RowNumber>=${pager.startIndex}

mapper.insert:

	SELECT @@IDENTITY

INSERT INTO SHOP(
	title, keyword, description,
	contents, address, tel,
	contacter, imagePath, siteId
) VALUES (
	#{title}, #{keyword}, #{description},
	#{contents}, #{address}, #{tel},
	#{contacter}, #{imagePath}, #{siteId}
)

insert.foreach:
INSERT INTO SHOPTYPE(
	shopId, typeId
) VALUES (
	#{id}, #{type.id}
)
